 running_vms = 0

 for server in hs_manage.engine.values():
 total_vms += len(server.vm_saving)
 # 统计运行中的虚拟机数量（根据实际状态判断）

 return api_response(200, 'success', {
 'host_count': len(hs_manage.engine),
 'vm_count': total_vms,
 'running_vm_count': running_vms
 })


