def init_app():
    """初始化应用"""
    # 加载已保存的配置
    try:
        hs_manage.all_load()
    except Exception as e:
        print(f"加载配置失败: {e}")

    # 如果没有Token，生成一个
    if not hs_manage.bearer:
        hs_manage.set_pass()
        print(f"已生成访问Token: {hs_manage.bearer}")


if __name__ == '__main__':
